import logo from './logo.svg';
import './App.css';
import './css/main.css';
import { React, useState } from "react";
import axios from "axios";

function App() {
  const [shortenedLink, setShortenedLink] = useState("");
  const [userInput, setUserInput] = useState("");
  //const [cars, setCars] = useState(['Ford', 'BMW', 'Audi']);

  var savedLinks = JSON.parse(window.localStorage.getItem('users_short_links'));

  const [shortenedLinks, setShortenedLinks] = useState(savedLinks ?? []); //https://usefulangle.com/post/281/javascript-default-value-if-null-or-undefined

  //setShortenedLinks();

  //setCars(['Ford', 'BMW', 'Audi'])

  //const cars = ['Ford', 'BMW', 'Audi'] = useState([]);
  //cars.push( JSON.parse(window.localStorage.getItem('count')));
  //setCount(JSON.parse(window.localStorage.getItem('count')));


  const fetchData = async () => {

    var x =  {
      "orig_url": "red",
      "short_url": "123456",
      "time_created": Date.now()
    };
    var newShortenedLinks = shortenedLinks.slice();   //https://stackoverflow.com/questions/26505064/what-is-the-best-way-to-add-a-value-to-an-array-in-state
    newShortenedLinks.push( x);
    //var newShortenedLinks = [x, ...shortenedLinks];
    //var newShortenedLinks = shortenedLinks;//[];//
    window.localStorage.setItem('users_short_links', JSON.stringify(newShortenedLinks));
    //shortenedLinks.useState(shortenedLinks);
   // debugger;
   setShortenedLinks(newShortenedLinks); //https://bobbyhadz.com/blog/react-map-array-reverse
   //setValue([...value, newvalue]);

    /*try {
      const response = await axios(
        `https://api.shrtco.de/v2/shorten?url=${userInput}`
      );
      setShortenedLink(response.data.result.full_short_link);
      var x =  {
        "color": "red",
        "type": response.data.result.full_short_link,
        "registration": new Date('2018-03-03'),
        "capacity": 5
      };

      cars.push( x);
      window.localStorage.setItem('count', JSON.stringify(x));
      debugger;
    } catch (e) {
      console.log(e);
    }*/
  };
  return (
    <div>
        <ol style={{'flex-direction': 'row-reverse'}}>
        {
        shortenedLinks.map((link, index) => (
        <li id={link.time_created} key={link.time_created}>{link.orig_url} {link.orig_url}<button onClick={() => {navigator.clipboard.writeText(this.state.shortenedLinks[index].time_created)}}>Copy to clip</button></li>
        ))}
        </ol>
          <input
            className="outline-none border-2 border-blue-500 rounded-md backdrop-blur-xl bg-white/20 shadow-md px-3 py-3"
            type="text"
            placeholder="Enter link to be shortened"
            value={userInput}
            onChange={(e) => {
              setUserInput(e.target.value);
            }}
          />
          <button
            className=" bg-blue-500 text-white px-8 py-3 ml-4 rounded-md"
            onClick={() => {
              fetchData();
            }}
          >
            Submit URL
          </button>
    </div>
  );
}

export default App;
