/*
npm run build-css src/scss/styles.scss
*/
import logo from './logo.svg';
import './App.css';
import './css/main.css';
import { React, useState } from "react";
import axios from "axios";

function App() {

  //<meta name="viewport" content="width=device-width, initial-scale=1"> https://getbootstrap.com/docs/3.4/css/

  const [userInput, setUserInput] = useState("");
  var savedLinks = JSON.parse(window.localStorage.getItem('users_short_links'));
  const [shortenedLinks, setShortenedLinks] = useState(savedLinks ?? []); //https://usefulangle.com/post/281/javascript-default-value-if-null-or-undefined

  const fetchData = async () => {

    try {
      const response = await axios(
        `https://api.shrtco.de/v2/shorten?url=${userInput}`
      );

      var newLinkJSON =  {
        "orig_url": userInput,
        "short_url": response.data.result.full_short_link,
        "time_created": Date.now()
      };

      var newShortenedLinks = shortenedLinks.slice();   //https://stackoverflow.com/questions/26505064/what-is-the-best-way-to-add-a-value-to-an-array-in-state
      newShortenedLinks.push(newLinkJSON);
      window.localStorage.setItem('users_short_links', JSON.stringify(newShortenedLinks));
      setShortenedLinks(newShortenedLinks); //https://bobbyhadz.com/blog/react-map-array-reverse

    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div>
        <table class="table table-striped">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td>Larry</td>
      <td>the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>
        <ol>
        {
        shortenedLinks.map((link, index) => (
        <li id={link.time_created} key={link.time_created}>{link.orig_url} {link.short_url}
        <button class="btn btn-primary" onClick={() => {navigator.clipboard.writeText(shortenedLinks[index].short_url)}}>Copy to clip</button></li>
        ))}
        </ol>
          <input
            className="form-control"
            type="text"
            placeholder="Enter link to be shortened"
            value={userInput}
            onChange={(e) => {
              setUserInput(e.target.value);
            }}
          />

          <button
            className="btn btn-primary"
            onClick={() => {
              fetchData();
            }}
          >
            Submit URL
          </button>
    </div>
  );
}

export default App;
